test_that("EncodeChrom", {
    res <- mapply(EncodeChrom, v.chrom, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.kchrom))
})

test_that("DecodeChrom", {
    res <- mapply(DecodeChrom, v.kchrom, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.nchrom))
})

test_that("EncodeRefAlt", {
    res <- mapply(EncodeRefAlt, v.ref, v.alt, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.refalt))
})

test_that("DecodeRefAlt", {
    res <- mapply(DecodeRefAlt, v.refalt, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(unlist(res[1,]), equals(v.rref))
    expect_that(unlist(res[2,]), equals(v.ralt))
})

test_that("EncodeVariantKey", {
    res <- mapply(EncodeVariantKey, v.kchrom, v.pos, v.refalt, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_identical(res, v.nvk)
})

test_that("ExtractVariantkeyChrom", {
    res <- mapply(ExtractVariantkeyChrom, v.nvk, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.kchrom))
})

test_that("ExtractVariantkeyPos", {
    res <- mapply(ExtractVariantkeyPos, v.nvk, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.pos))
})

test_that("ExtractVariantkeyRefAlt", {
    res <- mapply(ExtractVariantkeyRefAlt, v.nvk, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.refalt))
})

test_that("DecodeVariantkey", {
    res <- mapply(DecodeVariantkey, v.nvk, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(unlist(res[1,]), equals(v.kchrom))
    expect_that(unlist(res[2,]), equals(v.pos))
    expect_that(unlist(res[3,]), equals(v.refalt))
})

test_that("VariantKey", {
    res <- mapply(VariantKey, v.chrom, v.pos, v.ref, v.alt, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_identical(res, v.nvk)
})

test_that("VariantKeyRange", {
    res <- VariantKeyRange(1, 0, 268435455)
    expect_that(res$MIN, equals(0x0800000000000000))
    expect_that(res$MAX, equals(0x0fffffffffffffff))
})

test_that("CompareVariantkeyChrom", {
    res <- mapply(CompareVariantkeyChrom, 0x08027a3c08e80000, 0x100036cc08900000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(-1))
    res <- mapply(CompareVariantkeyChrom, 0x0fffffff88b80000, 0x08027a2188c80000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(0))
    res <- mapply(CompareVariantkeyChrom, 0x100036cc08900000, 0x08027a3c08e80000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(1))
})

test_that("CompareVariantkeyChromPos", {
    res <- mapply(CompareVariantkeyChromPos, 0x08027a3c08e80000, 0x100036cc08900000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(-1))
    res <- mapply(CompareVariantkeyChromPos, 0x100036cc08900000, 0x08027a3c08e80000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(1))
    res <- mapply(CompareVariantkeyChromPos, 0x08027a2588b00000, 0x0fffffff88b80000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(-1))
    res <- mapply(CompareVariantkeyChromPos, 0x0fffffff88b80000, 0x0fffffff8ae2503b, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(0))
    res <- mapply(CompareVariantkeyChromPos, 0x0fffffff88b80000, 0x08027a2588b00000, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(1))
})

test_that("VariantkeyHex", {
    res <- mapply(VariantkeyHex, v.nvk, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.vk))
})

test_that("ParseVariantkeyHex", {
    res <- mapply(ParseVariantkeyHex, v.vk, SIMPLIFY = TRUE, USE.NAMES = FALSE)
    expect_that(res, equals(v.nvk))
})
